class DropDownWidget<T> extends StatefulWidget {
  final List<CustomDropdownMenuItem> items;
  final Function onChanged;
  final String hintText;
  final double borderRadius;
  final double maxListHeight;
  final double borderWidth;
  final int defaultSelectedIndex;
  final bool enabled;
  final Widget icon;
  final bool showText;
  final double? menuItemWidth;
  final bool showLeftSide;

  const DropDownWidget(
      {required this.items,
        required this.onChanged,
        this.hintText = "",
        this.borderRadius = 0,
        this.borderWidth = 1,
        this.maxListHeight = 100,
        this.defaultSelectedIndex = -1,
        super.key,
        this.enabled = true,
        required this.icon,
        this.showText = true,
        this.menuItemWidth,
        this.showLeftSide = true
      });

  @override
  DropDownWidgetState createState() => DropDownWidgetState();
}

class DropDownWidgetState extends State<DropDownWidget> with WidgetsBindingObserver {
  bool _isOpen = false, _isAnyItemSelected = false, _isReverse = false;
  late OverlayEntry _overlayEntry;
  late RenderBox? _renderBox;
  Widget? _itemSelected;
  late Offset dropDownOffset;
  final LayerLink _layerLink = LayerLink();

  @override
  void initState() {
    WidgetsBinding.instance.addPostFrameCallback((_) {
      if (mounted) {
        setState(() {
          dropDownOffset = getOffset();
        });
      }
      if (widget.defaultSelectedIndex > -1) {
        if (widget.defaultSelectedIndex < widget.items.length) {
          if (mounted) {
            setState(() {
              _isAnyItemSelected = true;
              _itemSelected = widget.items[widget.defaultSelectedIndex];
              widget.onChanged(widget.items[widget.defaultSelectedIndex].value);
            });
          }
        }
      }
    });
    WidgetsBinding.instance.addObserver(this);
    super.initState();
  }

  void _addOverlay() {
    if (mounted) {
      setState(() {
        _isOpen = true;
      });
    }

    _overlayEntry = _createOverlayEntry();
    Overlay.of(context).insert(_overlayEntry);
  }

  void _removeOverlay() {
    if (mounted) {
      setState(() {
        _isOpen = false;
      });
      _overlayEntry.remove();
    }
  }

  @override
  dispose() {
    WidgetsBinding.instance.removeObserver(this);
    super.dispose();
  }

  OverlayEntry _createOverlayEntry() {
    _renderBox = context.findRenderObject() as RenderBox?;

    var size = _renderBox!.size;

    dropDownOffset = getOffset();

    return OverlayEntry(
        maintainState: false,
        builder: (context) => Align(
          alignment: Alignment.center,
          child: CompositedTransformFollower(
            link: _layerLink,
            showWhenUnlinked: false,
            offset: dropDownOffset,
            child: SizedBox(
              height: widget.maxListHeight,
              width: widget.menuItemWidth ?? size.width,
              child: Column(
                mainAxisSize: MainAxisSize.max,
                mainAxisAlignment: _isReverse
                    ? MainAxisAlignment.end
                    : MainAxisAlignment.start,
                children: <Widget>[
                  Padding(padding: const EdgeInsets.only(top: 5),
                    child: Container(
                      constraints: BoxConstraints(maxHeight: widget.maxListHeight,
                          maxWidth: widget.menuItemWidth ?? size.width),
                      decoration: BoxDecoration(
                          color: Theme.of(context).cardColor,
                          borderRadius: BorderRadius.circular(12),
                        boxShadow: Get.isDarkMode ? null :shadow,
                      ),
                      child: ClipRRect(
                        borderRadius: BorderRadius.all(
                          Radius.circular(widget.borderRadius),
                        ),
                        child: Material(
                          elevation: 0,
                          color: Get.isDarkMode ? Theme.of(context).canvasColor : Theme.of(context).primaryColor.withValues(alpha:0.03),
                          shadowColor: Get.isDarkMode ? Colors.transparent : Colors.grey,
                          child: ListView(
                            padding: EdgeInsets.zero,
                            shrinkWrap: true,
                            children: widget.items
                                .map((item) => GestureDetector(
                              child: Column(
                                children: [
                                  const SizedBox(height: 7),
                                  item.child,
                                  const SizedBox(height: 7),
                              if( widget.items.indexOf(item) != widget.items.length - 1 )
                                Container(height: 1,width: 60,color: Get.isDarkMode ? Theme.of(context).hintColor :Theme.of(context).primaryColor.withValues(alpha:0.2),)
                                ],
                              ),
                              onTap: () {
                                if (mounted) {
                                  setState(() {
                                    _isAnyItemSelected = true;
                                    _itemSelected = item.child;
                                    _removeOverlay();
                                    widget.onChanged(item.value);
                                  });
                                }
                              },
                            ))
                                .toList(),
                          ),
                        ),
                      ),
                    ),
                  ),
                ],
              ),
            ),
          ),
        ));
  }

  Offset getOffset() {
    RenderBox? renderBox = context.findRenderObject() as RenderBox?;
    double y = renderBox!.localToGlobal(Offset.zero).dy;
    double spaceAvailable = _getAvailableSpace(y + renderBox.size.height);
    if (spaceAvailable > widget.maxListHeight) {
      _isReverse = false;
      return Offset(widget.showLeftSide ? 0 : renderBox.size.width - (widget.menuItemWidth ?? 0), renderBox.size.height);
    } else {
      _isReverse = true;
      return Offset(
          widget.showLeftSide ? 0 : renderBox.size.width - (widget.menuItemWidth ?? 0),
          renderBox.size.height -
              (widget.maxListHeight + renderBox.size.height));
    }
  }

  double _getAvailableSpace(double offsetY) {
    double safePaddingTop = MediaQuery.of(context).padding.top;
    double safePaddingBottom = MediaQuery.of(context).padding.bottom;

    double screenHeight =
        MediaQuery.of(context).size.height - safePaddingBottom - safePaddingTop;

    return screenHeight - offsetY;
  }

  @override
  Widget build(BuildContext context) {
    return CompositedTransformTarget(
      link: _layerLink,
      child: GestureDetector(
        onTap: widget.enabled
            ? () {
          _isOpen ? _removeOverlay() : _addOverlay();
        }
            : null,
        child: Container(
          decoration: _getDecoration(),
          child: Row(
            mainAxisAlignment: MainAxisAlignment.spaceBetween,
            children: <Widget>[
              widget.showText ?
              Flexible(
                flex: 3,
                child: _isAnyItemSelected ?
                Padding(
                  padding: const EdgeInsets.only(left: 4.0),
                  child: _itemSelected!,
                ) : Padding(
                  padding:
                  const EdgeInsets.only(left: 4.0), // change it here
                  child: Text(
                    widget.hintText,
                    maxLines: 1,
                    overflow: TextOverflow.clip,
                  ),
                ),
              ) :
              const SizedBox(),
               Flexible(
                flex: 1,
                child: widget.icon,
              ),
            ],
          ),
        ),
      ),
    );
  }

  Decoration? _getDecoration() {
    if (_isOpen && !_isReverse) {
      return BoxDecoration(
          borderRadius: BorderRadius.only(
              topLeft: Radius.circular(widget.borderRadius),
              topRight: Radius.circular(
                widget.borderRadius,
              )));
    } else if (_isOpen && _isReverse) {
      return BoxDecoration(
          borderRadius: BorderRadius.only(
              bottomLeft: Radius.circular(widget.borderRadius),
              bottomRight: Radius.circular(
                widget.borderRadius,
              )));
    } else if (!_isOpen) {
      return BoxDecoration(
          borderRadius: BorderRadius.all(Radius.circular(widget.borderRadius)));
    }
    return null;
  }
}

