    if(!GetPlatform.isWeb){

        FirebaseMessaging.instance.subscribeToTopic(AppConstants.topic);

    }
    return await apiClient.postData(AppConstants.fcmTokenUpdate, {"_method": "put", "fcm_token": deviceToken});
