class SignUpScreen extends StatefulWidget {
  const SignUpScreen({super.key});

  @override
  State<SignUpScreen> createState() => _SignUpScreenState();
}

class _SignUpScreenState extends State<SignUpScreen> {

  TextEditingController fNameController = TextEditingController();
  TextEditingController lNameController = TextEditingController();
  TextEditingController phoneController = TextEditingController();
  TextEditingController passwordController = TextEditingController();
  TextEditingController confirmPasswordController = TextEditingController();
  TextEditingController referralCodeController = TextEditingController();

  FocusNode fNameNode = FocusNode();
  FocusNode lNameNode = FocusNode();
  FocusNode phoneNode = FocusNode();
  FocusNode confirmPasswordNode = FocusNode();
  FocusNode passwordNode = FocusNode();
  FocusNode referralNode = FocusNode();


  @override
  void initState() {
    super.initState();

    Get.find<AuthController>().countryDialCode = CountryCode.fromCountryCode(Get.find<ConfigController>().config!.countryCode!).dialCode!;
  }

  @override
  Widget build(BuildContext context) {
    return SafeArea(child: Scaffold(
      backgroundColor: Theme.of(context).canvasColor,
      body: GetBuilder<AuthController>(builder: (authController){
        return Center(child: SingleChildScrollView(
            child: Padding(padding: const EdgeInsets.all(Dimensions.paddingSizeLarge),
              child: Column(
                mainAxisAlignment: MainAxisAlignment.center,
                crossAxisAlignment: CrossAxisAlignment.start,
                children: [
                  const SizedBox(height: Dimensions.paddingSizeSmall),

                  FutureBuilder<String>(
                      future: loadSvgAndChangeColors(Images.signUpScreenLogoSvg, Theme.of(context).primaryColor),
                      builder: (context, snapshot){
                        if (snapshot.connectionState == ConnectionState.done && snapshot.hasData) {
                          return SvgPicture.string(
                              snapshot.data!
                          );
                        }
                        return SvgPicture.asset(Images.signUpScreenLogoSvg);
                      }
                  ),
                  const SizedBox(height: Dimensions.paddingSizeDefault),

                  Text(
                    'sign_up'.tr,
                    style: textBold.copyWith(fontSize: Dimensions.fontSizeTwenty),
                  ),
                  const SizedBox(height: Dimensions.paddingSizeExtraSmall),

                  Text(
                    'sign_up_message'.tr,
                    style: textMedium.copyWith(color: Theme.of(context).textTheme.bodyMedium?.color?.withValues(alpha: 0.7),fontSize: Dimensions.fontSizeSmall),
                    maxLines: 2,
                  ),
                  const SizedBox(height: Dimensions.paddingSizeExtraLarge),

                  TextFieldTitle(title: 'first_name'.tr, isRequired: true),

                  CustomTextField(
                    capitalization: TextCapitalization.words,
                    hintText: 'first_name'.tr,
                    inputType: TextInputType.name,
                    prefixIcon: Images.person,
                    controller: fNameController,
                    focusNode: fNameNode,
                    nextFocus: lNameNode,
                    inputAction: TextInputAction.next,
                    autoFocus: fNameController.text.isEmpty,
                  ),
                  const SizedBox(width: Dimensions.paddingSizeDefault),

                  TextFieldTitle(title: 'last_name'.tr, isRequired: true),

                  CustomTextField(
                    capitalization: TextCapitalization.words,
                    hintText: 'last_name'.tr,
                    inputType: TextInputType.name,
                    prefixIcon: Images.person,
                    controller: lNameController,
                    focusNode: lNameNode,
                    nextFocus: phoneNode,
                    inputAction: TextInputAction.next,
                  ),

                  TextFieldTitle(title: 'phone'.tr, isRequired: true),

                  CustomTextField(
                    isCodePicker: true,
                    hintText: 'phone'.tr,
                    inputType: TextInputType.number,
                    countryDialCode: authController.countryDialCode,
                    controller: phoneController,
                    focusNode: phoneNode,
                    nextFocus: passwordNode,
                    inputAction: TextInputAction.next,
                    onCountryChanged: (CountryCode countryCode){
                      authController.countryDialCode = countryCode.dialCode!;
                      authController.setCountryCode(countryCode.dialCode!);
                      FocusScope.of(context).requestFocus(phoneNode);
                    },
                  ),

                  if(Get.find<ConfigController>().config?.referralEarningStatus ?? false)...[
                    TextFieldTitle(title: 'refer_code'.tr),

                    CustomTextField(
                      hintText: 'refer_code'.tr,
                      inputType: TextInputType.text,
                      controller: referralCodeController,
                      focusNode: referralNode,
                      inputAction: TextInputAction.done,
                      prefixIcon: Images.referIcon,
                    ),
                  ],

                  TextFieldTitle(title: 'password'.tr, isRequired: true),

                  CustomTextField(
                    hintText: 'password'.tr,
                    inputType: TextInputType.text,
                    prefixIcon: Images.password,
                    isPassword: true,
                    controller: passwordController,
                    focusNode: passwordNode,
                    nextFocus: confirmPasswordNode,
                    inputAction: TextInputAction.next,
                  ),

                  TextFieldTitle(title: 'confirm_password'.tr, isRequired: true),

                  CustomTextField(
                    hintText: 'confirm_password'.tr,
                    inputType: TextInputType.text,
                    prefixIcon: Images.password,
                    controller: confirmPasswordController,
                    focusNode: confirmPasswordNode,
                    nextFocus: referralNode,
                    inputAction: TextInputAction.next,
                    isPassword: true,
                  ),

                  const SizedBox(height: Dimensions.paddingSizeDefault * 2),

                  authController.isLoading ?
                  Center(child: SpinKitCircle(color: Theme.of(context).primaryColor, size: 40.0)) :
                  ButtonWidget(
                    buttonText: 'submit'.tr,
                    radius: 50,
                    onPressed: () {
                      String fName = fNameController.text.trim();
                      String lName = lNameController.text.trim();
                      String phone = phoneController.text.trim();
                      String password = passwordController.text.trim();
                      String confirmPassword = confirmPasswordController.text.trim();

                      if(fName.isEmpty) {
                        showCustomSnackBar('first_name_is_required'.tr);
                        FocusScope.of(context).requestFocus(fNameNode);
                      }else if(lName.isEmpty) {
                        showCustomSnackBar('last_name_is_required'.tr);
                        FocusScope.of(context).requestFocus(lNameNode);
                      }else if(phone.isEmpty) {
                        showCustomSnackBar('phone_is_required'.tr);
                        FocusScope.of(context).requestFocus(phoneNode);
                      }else if(!PhoneNumber.parse(authController.countryDialCode + phone).isValid(type: PhoneNumberType.mobile)) {
                        showCustomSnackBar('phone_number_is_not_valid'.tr);
                        FocusScope.of(context).requestFocus(phoneNode);
                      }else if(password.isEmpty) {
                        showCustomSnackBar('password_is_required'.tr);
                        FocusScope.of(context).requestFocus(passwordNode);
                      }else if(password.length < 8) {
                        showCustomSnackBar('minimum_password_length_is_8'.tr);
                        FocusScope.of(context).requestFocus(passwordNode);
                      }else if(confirmPassword.isEmpty) {
                        showCustomSnackBar('confirm_password_is_required'.tr);
                        FocusScope.of(context).requestFocus(confirmPasswordNode);
                      }else if(password != confirmPassword) {
                        showCustomSnackBar('password_is_mismatch'.tr);
                        FocusScope.of(context).requestFocus(confirmPasswordNode);
                      } else{
                        authController.register(SignUpBody(
                          fName: fName,
                          lName: lName,
                          phone: authController.countryDialCode + phone,
                          password: password,
                          confirmPassword: confirmPassword,
                          referralCode: referralCodeController.text.trim()
                        ));
                      }
                    },
                  ),
                  const SizedBox(height: Dimensions.paddingSizeDefault),

                  Row(mainAxisAlignment: MainAxisAlignment.center, children: [
                      Text(
                        '${'already_have_an_account'.tr} ',
                          style: textRegular.copyWith(fontSize: Dimensions.fontSizeSmall,
                              color: Theme.of(context).textTheme.bodyLarge!.color,
                          ),
                      ),

                      TextButton(
                          onPressed: () =>  Get.back(),
                          style: TextButton.styleFrom(
                              padding: EdgeInsets.zero, minimumSize: const Size(50,30),
                              tapTargetSize: MaterialTapTargetSize.shrinkWrap,
                          ),
                          child: Text('login'.tr, style: textRegular.copyWith(
                              decoration: TextDecoration.underline, color: Theme.of(context).primaryColor,
                              decorationColor: Theme.of(context).primaryColor
                          )),
                      ),
                  ]),
                  const SizedBox(height: Dimensions.paddingSizeDefault),
                ],
              ),
            ),
          ));
      }),
    ));
  }
}

import 'dart:async';
import 'package:flutter/material.dart';
import 'package:flutter_spinkit/flutter_spinkit.dart';
import 'package:flutter_svg/svg.dart';
import 'package:get/get.dart';
import 'package:pin_code_fields/pin_code_fields.dart';
import 'package:ride_sharing_user_app/helper/display_helper.dart';
import 'package:ride_sharing_user_app/helper/svg_image_helper.dart';
import 'package:ride_sharing_user_app/core/constants/dimensions.dart';
import 'package:ride_sharing_user_app/core/constants/images.dart';
import 'package:ride_sharing_user_app/core/constants/styles.dart';
import 'package:ride_sharing_user_app/features/auth/controllers/auth_controller.dart';
import 'package:ride_sharing_user_app/features/splash/controllers/config_controller.dart';
import 'package:ride_sharing_user_app/common_widgets/app_bar_widget.dart';
import 'package:ride_sharing_user_app/common_widgets/body_widget.dart';
import 'package:ride_sharing_user_app/common_widgets/button_widget.dart';

class VerificationScreen extends StatefulWidget {
  final String number;
  final bool fromOtpLogin;
  final String? session;
  const VerificationScreen({super.key,required this.number,  this.fromOtpLogin = false, this.session});

  @override
  VerificationScreenState createState() => VerificationScreenState();
}

class VerificationScreenState extends State<VerificationScreen> {
  Timer? _timer;
  int? _seconds = 0;
  final StreamController<ErrorAnimationType> _errorController = StreamController<ErrorAnimationType>();
  String errorText = '';

  @override
  void initState() {
    super.initState();
    Get.find<AuthController>().updateVerificationCode('',isUpdate: false);
    _startTimer();
  }

  void _startTimer() {
    _seconds = Get.find<ConfigController>().config!.otpResendTime;
    _timer = Timer.periodic(const Duration(seconds: 1), (timer) {
      _seconds = _seconds! - 1;
      if(_seconds == 0) {
        timer.cancel();
        _timer?.cancel();
      }
      setState(() {});
    });
  }

  @override
  void dispose() {
    super.dispose();
    _errorController.close();
    _timer?.cancel();
  }

  @override
  Widget build(BuildContext context) {
    return SafeArea(
      top: false,
      child: Scaffold(
        body: BodyWidget(
          appBar: AppBarWidget(title: 'verification'.tr, showBackButton: true, centerTitle: true),
          body: Center(child: SingleChildScrollView(
            physics: const BouncingScrollPhysics(),
            padding: const EdgeInsets.symmetric(horizontal:Dimensions.paddingSizeLarge),
            child: GetBuilder<AuthController>(builder: (authController) {
              return Column(
                crossAxisAlignment: CrossAxisAlignment.start,
                children: [
                  const SizedBox(height: Dimensions.paddingSizeExtraLarge),

                  FutureBuilder<String>(
                      future: loadSvgAndChangeColors(Images.verificationGraphics, Theme.of(context).primaryColor),
                      builder: (context, snapshot){
                        if (snapshot.connectionState == ConnectionState.done && snapshot.hasData) {
                          return SvgPicture.string(
                              snapshot.data!
                          );
                        }
                        return SvgPicture.asset(Images.verificationGraphics);
                      }
                  ),
                  const SizedBox(height: Dimensions.paddingSizeSmall),

                  Text(
                    'verification'.tr,
                    style: textBold.copyWith(fontSize: Dimensions.fontSizeTwenty),
                  ),
                  const SizedBox(height: Dimensions.paddingSizeExtraSmall),

                  Text(
                    '${'we_have_send_a_varification_code_to'.tr} ${widget.number.substring(0, 5)}*****${widget.number.substring(widget.number.length - 3, widget.number.length)}',
                    style: textMedium.copyWith(color: Theme.of(context).textTheme.bodyMedium?.color?.withValues(alpha: 0.7),fontSize: Dimensions.fontSizeSmall),
                    maxLines: 2,
                  ),
                  const SizedBox(height: Dimensions.paddingSizeSignUp),

                  (Get.find<ConfigController>().config?.isDemo ?? true) ? 
                  Padding(
                    padding: const EdgeInsets.all(Dimensions.paddingSizeSmall).copyWith(
                      bottom: Dimensions.paddingSizeOverLarge,
                    ),
                    child: Text('for_demo_purpose_use'.tr, style: textSemiBold.copyWith(
                      color: Theme.of(context).disabledColor,
                    )),
                  ) : 
                  const SizedBox(height:  Dimensions.paddingSizeDefault),

                  SizedBox(
                    width: Get.width - 60,
                    child: PinCodeTextField(
                      length: 6,
                      cursorColor: Theme.of(context).textTheme.bodyMedium?.color,
                      cursorHeight: 20,
                      autoFocus: true,
                      appContext: context,
                      keyboardType: TextInputType.number,
                      animationType: AnimationType.slide,
                      errorAnimationController: _errorController,
                      pinTheme: PinTheme(
                        shape: PinCodeFieldShape.box,
                        fieldHeight: 40,
                        fieldWidth: 40,
                        borderWidth: 1,
                        borderRadius: BorderRadius.circular(Dimensions.radiusDefault),
                        selectedColor: Theme.of(context).hintColor.withValues(alpha:0.2),
                        selectedFillColor: Get.isDarkMode ? Colors.grey.withValues(alpha:0.6) : Colors.white,
                        inactiveFillColor: Theme.of(context).cardColor,
                        inactiveColor: Theme.of(context).hintColor.withValues(alpha:0.2),
                        activeColor: Theme.of(context).primaryColor.withValues(alpha: 0.5),
                        activeFillColor: Theme.of(context).cardColor,
                        errorBorderColor: Colors.red,
                      ),
                      animationDuration: const Duration(milliseconds: 300),
                      backgroundColor: Colors.transparent,
                      enableActiveFill: true,
                      onChanged: (query){
                        errorText = '';
                        authController.updateVerificationCode(query);
                      },
                      beforeTextPaste: (text) => true,
                      textStyle: textSemiBold.copyWith(),

                      pastedTextStyle: textRegular.copyWith(color: Theme.of(context).textTheme.bodyMedium!.color),
                    ),
                  ),

                  if(errorText.isNotEmpty)
                    Center(child: Text(errorText, style: textRegular.copyWith(color: Theme.of(context).colorScheme.error))),

                  !authController.otpVerifying ?
                  Padding(
                    padding: const EdgeInsets.only(top: Dimensions.paddingSizeSmall),
                    child: ButtonWidget(
                      buttonText: 'verify'.tr,
                      radius: 50,
                      textColor: authController.verificationCode.length == 6 ? Theme.of(context).cardColor : Theme.of(context).textTheme.bodyMedium?.color,
                      onPressed: authController.verificationCode.length == 6 ? () {
                        authController.otpVerification(
                            widget.number, authController.verificationCode, accountVerification: widget.fromOtpLogin,
                            session: widget.session
                        ).then((value){
                          if(value.statusCode != 200){
                            errorText = 'incorrect_otp'.tr;
                            _errorController.add(ErrorAnimationType.shake);
                          }
                        });
                      } : null,
                    ),
                  ) :
                  SpinKitCircle(color: Theme.of(context).primaryColor, size: 40.0),
                  const SizedBox(height: Dimensions.paddingSizeSmall),

                  Row(
                    mainAxisAlignment: MainAxisAlignment.spaceBetween,
                    crossAxisAlignment: CrossAxisAlignment.center,
                    children: [
                      Text('did_not_receive_the_code'.tr,style: textMedium.copyWith(color: Theme.of(context).textTheme.bodyMedium!.color!.withValues(alpha:.6))),

                      _seconds! <= 0 ?
                      TextButton(
                        onPressed: () async {
                          if(Get.find<ConfigController>().config?.isFirebaseOtpVerification ?? false){

                            await authController.firebaseOtpSend(widget.number,canRoute: false);
                            showCustomSnackBar('otp_sent_successfully'.tr, isError: false);

                            _startTimer();

                          }else if(Get.find<ConfigController>().config?.isSmsGateway ?? false){
                            authController.sendOtp(widget.number).then((value){
                              if(value.statusCode == 200) {
                                _startTimer();
                              }
                            });
                          }else{
                            showCustomSnackBar('sms_gateway_not_integrate'.tr);
                          }
                        },
