    Response? response = await bannerRepo.updateBannerClickCount(bannerId);
    if(response!.statusCode == 200){

    }else{
      ApiChecker.checkApi(response);
    }
    update();
  }

  void setCurrentIndex(int index, bool notify) {
    _currentIndex = index;
    if(notify) {
      update();
    }
  }
}

