import 'package:get/get.dart';

import 'package:ride_sharing_user_app/features/my_level/domain/model/level_model.dart';
import 'package:ride_sharing_user_app/features/my_level/domain/services/level_service_interface.dart';

import '../../../core/network/api_checker.dart';

class LevelController extends GetxController implements GetxService{
  final LevelServiceInterface levelServiceInterface;

  LevelController({required this.levelServiceInterface});

  LevelModel? _levelModel;
  LevelModel? get levelModel => _levelModel;

  Future<void> getProfileLevelInfo() async{
    Response response = await levelServiceInterface.getProfileLevelInfo();
    if(response.statusCode == 200){
      _levelModel = LevelModel.fromJson(response.body);
    }else{
      ApiChecker.checkApi(response);
    }
    update();
  }

}

class LevelModel {
  String? responseCode;
  String? message;
  int? totalSize;
  int? limit;
  int? offset;
  Data? data;
  List<String>? errors;

  LevelModel(
      {this.responseCode,
        this.message,
        this.totalSize,
        this.limit,
        this.offset,
        this.data,
        this.errors});

  LevelModel.fromJson(Map<String, dynamic> json) {
    responseCode = json['response_code'];
    message = json['message'];
    totalSize = json['total_size'];
    limit = json['limit'];
    offset = json['offset'];
    data = json['data'] != null ?  Data.fromJson(json['data']) : null;
    errors = json['errors'].cast<String>();
  }

}

class Data {
  NextLevel? nextLevel;
  CurrentLevel? currentLevel;
  CompletedCurrentLevelTarget? completedCurrentLevelTarget;
  bool? isCompleted;

  Data({this.nextLevel, this.currentLevel, this.completedCurrentLevelTarget,this.isCompleted});

  Data.fromJson(Map<String, dynamic> json) {
    nextLevel = json['next_level'] != null
        ?  NextLevel.fromJson(json['next_level'])
        : null;
    currentLevel = json['current_level'] != null
        ?  CurrentLevel.fromJson(json['current_level'])
        : null;
    completedCurrentLevelTarget = json['completed_current_level_target'] != null
        ?  CompletedCurrentLevelTarget.fromJson(
        json['completed_current_level_target'])
        : null;
    isCompleted = json['level_completed'];
  }

}

