class OnBoardController extends GetxController  implements GetxService{
  int pageIndex = 0;

  void onPageChanged(int index){
    pageIndex = index;
    update();
  }

  void onPageIncrement(){
    if(AppConstants.onBoardPagerData.length-1>pageIndex){
      pageIndex++;
    }
    update();
  }
}

class OnBoardingModel {
  String title1;
  String title2;
