import '../../../core/network/api_checker.dart';

class ReferAndEarnController extends GetxController implements GetxService {
  final ReferEarnServiceInterface referEarnServiceInterface;
  ReferAndEarnController({required this.referEarnServiceInterface});

  List<String> referAndEarnType = ['referral_details','earning'];
  int currentTabIndex = 0;
  bool isLoading = false;
  ScrollController scrollController = ScrollController();

  void updateCurrentTabIndex(int index, {bool isUpdate = false}){
    currentTabIndex = index;
    if(isUpdate){
      update();
    }
  }

  TransactionModel? referralModel;
  ReferralDetailsModel? referralDetails;

  Future<Response> getEarningHistoryList (int offset) async{
    isLoading = true;
    update();
    Response response = await referEarnServiceInterface.getEarningHistoryList(offset);
    if (response.statusCode == 200) {
      isLoading = false;
      if(offset == 1){
        referralModel = TransactionModel.fromJson(response.body);
      }else{
        referralModel!.data!.addAll(TransactionModel.fromJson(response.body).data!);
        referralModel!.offset = TransactionModel.fromJson(response.body).offset;
        referralModel!.totalSize = TransactionModel.fromJson(response.body).totalSize;
      }

    }else{
      isLoading = false;
      ApiChecker.checkApi(response);
    }
    update();
    return response;
  }

  Future<void> getReferralDetails() async{
    isLoading = true;
    update();
    Response response = await referEarnServiceInterface.getReferralDetails();
    if(response.statusCode == 200){
      referralDetails = ReferralDetailsModel.fromJson(response.body);
      isLoading = false;
    }else{
      isLoading = false;
      ApiChecker.checkApi(response);
    }
    update();
  }
}

class ReferralDetailsModel {
  String? responseCode;
  String? message;
  int? totalSize;
  int? limit;
  int? offset;
  Data? data;
  List<String>? errors;

  ReferralDetailsModel(
      {this.responseCode,
        this.message,
        this.totalSize,
        this.limit,
        this.offset,
        this.data,
        this.errors});

  ReferralDetailsModel.fromJson(Map<String, dynamic> json) {
    responseCode = json['response_code'];
    message = json['message'];
    totalSize = json['total_size'];
    limit = json['limit'];
    offset = json['offset'];
    data = json['data'] != null ?  Data.fromJson(json['data']) : null;
    errors = json['errors'].cast<String>();
  }

  Map<String, dynamic> toJson() {
    final Map<String, dynamic> data = <String, dynamic>{};
    data['response_code'] = responseCode;
    data['message'] = message;
    data['total_size'] = totalSize;
    data['limit'] = limit;
    data['offset'] = offset;
    if (this.data != null) {
      data['data'] = this.data!.toJson();
    }
    data['errors'] = errors;
    return data;
  }
}

class Data {
  String? referralCode;
  double? shareCodeEarning;
  bool? firstRideDiscountStatus;
  double? discountAmount;
  String? discountAmountType;
  int? discountValidity;
  String? discountValidityType;

  Data(
      {this.referralCode,
        this.shareCodeEarning,
        this.firstRideDiscountStatus,
        this.discountAmount,
        this.discountAmountType,
        this.discountValidity,
        this.discountValidityType});

  Data.fromJson(Map<String, dynamic> json) {
    referralCode = json['referral_code'];
    shareCodeEarning = double.parse(json['share_code_earning'].toString());
    firstRideDiscountStatus = json['first_ride_discount_status'];
    discountAmount = double.parse(json['discount_amount'].toString());
    discountAmountType = json['discount_amount_type'];
    discountValidity = json['discount_validity'];
    discountValidityType = json['discount_validity_type'];
  }

  Map<String, dynamic> toJson() {
    final Map<String, dynamic> data = <String, dynamic>{};
    data['referral_code'] = referralCode;
    data['share_code_earning'] = shareCodeEarning;
    data['first_ride_discount_status'] = firstRideDiscountStatus;
    data['discount_amount'] = discountAmount;
    data['discount_amount_type'] = discountAmountType;
    data['discount_validity'] = discountValidity;
    data['discount_validity_type'] = discountValidityType;
    return data;
  }
}

