class ScheduleDateTimePickerWidget extends StatefulWidget {
  final bool fromSetDestinationScreen;
  final String? tripId;
  const ScheduleDateTimePickerWidget({super.key, this.fromSetDestinationScreen = false, this.tripId});

  @override
  State<ScheduleDateTimePickerWidget> createState() => _ScheduleDateTimePickerWidgetState();
}

class _ScheduleDateTimePickerWidgetState extends State<ScheduleDateTimePickerWidget> {
  bool showDatePicker = true;
  bool canUpdate = true;
  @override
  void initState() {
    if(widget.tripId != null){
     final RideController rideController = Get.find<RideController>();
     rideController.setScheduleTripTime(DateTime.parse(rideController.tripDetails?.scheduledAt ?? ''));
     rideController.setScheduleTripDate(rideController.tripDetails?.scheduledAt ?? '');
    }
    super.initState();
  }


  @override
  Widget build(BuildContext context) {
    return Container(
      padding: EdgeInsets.all(Dimensions.paddingSizeSmall),
      decoration: BoxDecoration(
          color: Theme.of(context).cardColor,
          borderRadius: BorderRadius.only(topLeft: Radius.circular(Dimensions.paddingSizeLarge), topRight: Radius.circular(Dimensions.paddingSizeLarge))
      ),
      child: Column(mainAxisSize: MainAxisSize.min, spacing: Dimensions.paddingSizeSmall, children: [
        Row(mainAxisAlignment: MainAxisAlignment.spaceBetween, children: [
          showDatePicker ?
          const SizedBox() :
          InkWell(
            onTap: (){
              showDatePicker = true;
              canUpdate = true;
              setState(() {});
            },
            child: Padding(
              padding: const EdgeInsets.all(Dimensions.paddingSizeExtraSmall),
              child: Icon(Icons.arrow_back_outlined, size: 18),
            ),
          ),

          Container(
            decoration: BoxDecoration(
                color: Theme.of(context).primaryColor.withValues(alpha: 0.2),
                borderRadius: BorderRadius.circular(Dimensions.paddingSizeExtraSmall)
            ),
            height: Dimensions.paddingSizeExtraSmall, width: 30,
          ),

          InkWell(
            onTap: ()=> Get.back(),
            child: Container(
              decoration: BoxDecoration(
                color: Theme.of(context).hintColor.withValues(alpha:0.2),
                borderRadius: BorderRadius.circular(50),
              ),
              padding: const EdgeInsets.all(Dimensions.paddingSizeExtraSmall),

              child: Image.asset(Images.crossIcon,height: 10,width: 10),
            ),
          ),
        ]),
        const SizedBox(height: Dimensions.paddingSizeSmall),

        Text(showDatePicker ? 'select_pickup_date'.tr : 'select_pickup_time'.tr, style: textSemiBold),

        showDatePicker ?
        Container(
            padding: const EdgeInsets.only(top: Dimensions.paddingSizeSmall, left: Dimensions.paddingSizeSmall, right: Dimensions.paddingSizeSmall),
            decoration: BoxDecoration(
              color: Theme.of(context).hintColor.withValues(alpha: 0.08),
              borderRadius: BorderRadius.circular(Dimensions.paddingSizeSmall),
            ),
            child:SfDateRangePicker(
              backgroundColor: Colors.transparent,
              showNavigationArrow: true,
              onSelectionChanged: (DateRangePickerSelectionChangedArgs args){
                if(args.value != null){
                   Get.find<RideController>().setScheduleTripDate(DateFormat('yyyy-MM-dd').format(args.value));
                }
              },
              initialSelectedDate: RideControllerHelper.dateFormatToShow(Get.find<RideController>().scheduleTripDate),
              maxDate: DateTime.now().add(Duration(seconds: Get.find<ConfigController>().config?.advanceScheduleBookTime ?? 0)),
              minDate: DateTime.now().add(Duration(seconds: Get.find<ConfigController>().config?.minimumScheduleBookTime  ?? 0)),
              selectionMode: DateRangePickerSelectionMode.single,
              todayHighlightColor: Theme.of(context).primaryColor,
              selectionShape: DateRangePickerSelectionShape.circle,
              selectionTextStyle: textRegular.copyWith(color: Theme.of(context).cardColor),
              selectionColor: Theme.of(context).primaryColor,
              headerHeight: 50,
              toggleDaySelection: true,
              enablePastDates: false,
              headerStyle: DateRangePickerHeaderStyle(
                backgroundColor: Colors.transparent,
                textAlign: TextAlign.center,
                textStyle: textMedium.copyWith(fontSize: Dimensions.fontSizeLarge,
                  color: Theme.of(context).textTheme.bodyLarge!.color!,
                ),
              ),
              monthViewSettings: const DateRangePickerMonthViewSettings(
                dayFormat: 'EE',
                viewHeaderHeight: 40,
                firstDayOfWeek: 1,
                numberOfWeeksInView: 6,
              ),
              monthCellStyle: DateRangePickerMonthCellStyle(
                  todayTextStyle: TextStyle(
                      color: Theme.of(context).textTheme.bodyLarge!.color!
                  ),
                  disabledDatesDecoration: BoxDecoration(
                      color: Theme.of(context).hintColor.withValues(alpha: 0.1),
                      shape: BoxShape.circle
                  ),
                  cellDecoration: BoxDecoration(
                    color: Theme.of(context).cardColor,
                    shape: BoxShape.circle,
                  ),
                  todayCellDecoration: BoxDecoration(
                      border: Border.all(color: Theme.of(context).primaryColor),
                      shape: BoxShape.circle
                  )
              ),

            )
        ) :
        Container(
          decoration: BoxDecoration(
            color:  Theme.of(context).hintColor.withValues(alpha: 0.15),
            borderRadius: BorderRadius.circular(Dimensions.paddingSizeSmall),
          ),
          padding: const EdgeInsets.all(Dimensions.paddingSizeSmall),
          child: TimePickerSpinner(
            is24HourMode: false,
            normalTextStyle: textRegular.copyWith(color: Theme.of(context).hintColor,fontSize: Dimensions.fontSizeSmall),
            highlightedTextStyle: textMedium.copyWith(
              fontSize: Dimensions.fontSizeLarge, color: Theme.of(context).textTheme.bodyMedium!.color,
            ),
            alignment: Alignment.topCenter,
            time: RideControllerHelper.timeFormatToShow(Get.find<RideController>().scheduleTripTime),
            isForce2Digits: true,
            itemHeight: Get.height * 0.06,
            itemWidth: Get.width * 0.2,
            onTimeChange: (time) {
               Get.find<RideController>().setScheduleTripTime(time);
               _checkScheduleTripCapable(true);
            },
            scrollOff: (value){
              _checkScheduleTripCapable(value);
            },
          ),
        ),

        GetBuilder<RideController>(builder: (rideController){
          return rideController.isLoading ?
          CircularProgressIndicator(color: Theme.of(context).primaryColor) :
            ButtonWidget(
            buttonText: showDatePicker ? 'next'.tr : widget.tripId != null ? 'update'.tr : 'save'.tr,
            onPressed: canUpdate ? (){
              if(showDatePicker){
                showDatePicker = false;
                _checkScheduleTripCapable(true);
              }else{
                if(widget.tripId != null){
                  rideController.updateScheduleTripTimeDate(widget.tripId).then((value){
                    Get.back();
                  });
                }else{
                  Get.back();
                  rideController.setRideType(RideType.scheduleRide, isUpdate: true);
                  if(!widget.fromSetDestinationScreen){
                    rideController.getEstimatedFare(false);
                  }
                }
              }

            } : null,
          );
        }),
        const SizedBox(height: Dimensions.paddingSizeSmall)

      ]),
    );
  }

  void  _checkScheduleTripCapable(bool canSave){
    DateTime scheduleDate = RideControllerHelper.dateFormatToShow(Get.find<RideController>().scheduleTripDate);
    DateTime scheduleTime = RideControllerHelper.timeFormatToShow(Get.find<RideController>().scheduleTripTime);

    DateTime finalDateTime = DateTime(scheduleDate.year,scheduleDate.month,scheduleDate.day,scheduleTime.hour,scheduleTime.minute,scheduleTime.second);

    int timeDifference = finalDateTime.difference(DateTime.now()).inSeconds;

    if((timeDifference >= (Get.find<ConfigController>().config?.minimumScheduleBookTime ?? 0) && canSave && (timeDifference < (Get.find<ConfigController>().config?.advanceScheduleBookTime ?? 0)))){
      setState(() {
        canUpdate = true;
      });
    }else{
      setState(() {
        canUpdate = false;
      });
    }
  }
}

import 'dart:math';
import 'package:flutter/material.dart';
import 'package:ride_sharing_user_app/core/constants/dimensions.dart';
import 'package:ride_sharing_user_app/core/constants/styles.dart';

class ItemScrollPhysics extends ScrollPhysics {
  final double? itemHeight;
  final double targetPixelsLimit;

  const ItemScrollPhysics({
    super.parent,
    this.itemHeight,
    this.targetPixelsLimit = 3.0,
  })  : assert(itemHeight != null && itemHeight > 0);

  @override
  ItemScrollPhysics applyTo(ScrollPhysics? ancestor) {
    return ItemScrollPhysics(
        parent: buildParent(ancestor), itemHeight: itemHeight);
  }

  double _getItem(ScrollPosition position) {
    double maxScrollItem =
    (position.maxScrollExtent / itemHeight!).floorToDouble();
    return min(max(0, position.pixels / itemHeight!), maxScrollItem);
  }

  double _getPixels(ScrollPosition position, double item) {
    return item * itemHeight!;
  }

  double _getTargetPixels(
      ScrollPosition position, Tolerance tolerance, double velocity) {
    double item = _getItem(position);
    if (velocity < -tolerance.velocity) {
      item -= targetPixelsLimit;
    } else if (velocity > tolerance.velocity) {
      item += targetPixelsLimit;
    }
    return _getPixels(position, item.roundToDouble());
  }

  @override
  Simulation? createBallisticSimulation(
      ScrollMetrics position, double velocity) {
    Tolerance tolerance = const Tolerance();
    final double target =
    _getTargetPixels(position as ScrollPosition, tolerance, velocity);
    if (target != position.pixels) {
      return ScrollSpringSimulation(spring, position.pixels, target, velocity,
          tolerance: tolerance);
    }
    return null;
  }

  @override
  bool get allowImplicitScrolling => false;
}

typedef SelectedIndexCallback = void Function(int);
typedef TimePickerCallback = void Function(DateTime);



class TimePickerSpinner extends StatefulWidget {
  final DateTime? time;
  final int minutesInterval;
  final int secondsInterval;
  final bool is24HourMode;
  final bool isShowSeconds;
  final TextStyle? highlightedTextStyle;
  final TextStyle? normalTextStyle;
  final double? itemHeight;
  final double? itemWidth;
  final AlignmentGeometry? alignment;
  final double? spacing;
  final bool isForce2Digits;
  final TimePickerCallback? onTimeChange;
  final Function(bool) scrollOff;

  const TimePickerSpinner(
      {super.key,
        this.time,
        this.minutesInterval = 1,
        this.secondsInterval = 1,
        this.is24HourMode = true,
        this.isShowSeconds = false,
        this.highlightedTextStyle,
        this.normalTextStyle,
        this.itemHeight,
        this.itemWidth,
        this.alignment,
        this.spacing,
        this.isForce2Digits = false,
        this.onTimeChange,
        required this.scrollOff
      });

  @override
  TimePickerSpinnerState createState() => TimePickerSpinnerState();
}

class TimePickerSpinnerState extends State<TimePickerSpinner> {
  ScrollController hourController = ScrollController();
  ScrollController minuteController = ScrollController();
  ScrollController secondController = ScrollController();
  ScrollController apController = ScrollController();
  int currentSelectedHourIndex = -1;
  int currentSelectedMinuteIndex = -1;
  int currentSelectedSecondIndex = -1;
  int currentSelectedAPIndex = 1;
  DateTime? currentTime;
  bool isHourScrolling = false;
  bool isMinuteScrolling = false;
  bool isSecondsScrolling = false;
  bool isAPScrolling = false;

  TextStyle defaultHighlightTextStyle =
  const TextStyle(fontSize: 32, color: Colors.black);
  TextStyle defaultNormalTextStyle =
  const TextStyle(fontSize: 32, color: Colors.black54);
  double defaultItemHeight = 60;
  double defaultItemWidth = 45;
  double defaultSpacing = 20;
  AlignmentGeometry defaultAlignment = Alignment.centerRight;

  TextStyle? _getHighlightedTextStyle() {
    return widget.highlightedTextStyle ?? defaultHighlightTextStyle;
  }

  TextStyle? _getNormalTextStyle() {
    return widget.normalTextStyle ?? defaultNormalTextStyle;
  }

  int _getHourCount() {
    return widget.is24HourMode ? 24 : 12;
  }

  int _getMinuteCount() {
    return (60 / widget.minutesInterval).floor();
  }

  int _getSecondCount() {
    return (60 / widget.secondsInterval).floor();
  }

  double? _getItemHeight() {
    return widget.itemHeight ?? defaultItemHeight;
  }

  double? _getItemWidth() {
    return widget.itemWidth ?? defaultItemWidth;
  }

  double? _getSpacing() {
    return widget.spacing ?? defaultSpacing;
  }

  AlignmentGeometry? _getAlignment() {
    return widget.alignment ?? defaultAlignment;
  }

  bool isLoop(int value) {
    return value > 10;
  }

  DateTime getDateTime() {
    int hour = currentSelectedHourIndex - _getHourCount();
    if (!widget.is24HourMode && currentSelectedAPIndex == 2) hour += 12;
    int minute = (currentSelectedMinuteIndex -
        (isLoop(_getMinuteCount()) ? _getMinuteCount() : 1)) *
        widget.minutesInterval;
    int second = (currentSelectedSecondIndex -
        (isLoop(_getSecondCount()) ? _getSecondCount() : 1)) *
        widget.secondsInterval;
    return DateTime(currentTime!.year, currentTime!.month, currentTime!.day,
        hour, minute, second);
  }

  @override
  void initState() {
    currentTime = widget.time ?? DateTime.now();

    currentSelectedHourIndex =
        (currentTime!.hour % (widget.is24HourMode ? 24 : 12)) + _getHourCount();
    hourController = ScrollController(
        initialScrollOffset:
        (currentSelectedHourIndex - 1) * _getItemHeight()!);

    currentSelectedMinuteIndex =
        (currentTime!.minute / widget.minutesInterval).floor() +
            (isLoop(_getMinuteCount()) ? _getMinuteCount() : 1);
    minuteController = ScrollController(
        initialScrollOffset:
        (currentSelectedMinuteIndex - 1) * _getItemHeight()!);

    currentSelectedSecondIndex =
        (currentTime!.second / widget.secondsInterval).floor() +
            (isLoop(_getSecondCount()) ? _getSecondCount() : 1);
    secondController = ScrollController(
        initialScrollOffset:
        (currentSelectedSecondIndex - 1) * _getItemHeight()!);

    currentSelectedAPIndex = currentTime!.hour >= 12 ? 2 : 1;
    apController = ScrollController(
        initialScrollOffset: (currentSelectedAPIndex - 1) * _getItemHeight()!);

    super.initState();

    if (widget.onTimeChange != null) {
      // WidgetsBinding.instance.addPostFrameCallback((_) => widget.onTimeChange!(getDateTime()));
    }
  }

  @override
  Widget build(BuildContext context) {
    List<Widget> contents = [
      SizedBox(
        width: _getItemWidth(),
        height: _getItemHeight()! * 2.9,
        child: spinner(
          hourController,
          _getHourCount(),
          currentSelectedHourIndex,
          isHourScrolling,
          1, (index) {
          currentSelectedHourIndex = index;
          isHourScrolling = true;
        }, () => isHourScrolling = false,
        ),
      ),
      Padding(padding: const EdgeInsets.symmetric(horizontal: Dimensions.paddingSizeSmall),
        child: Text(':',style: textMedium.copyWith(fontSize: Dimensions.fontSizeLarge),),
      ),
      SizedBox(
        width: _getItemWidth(),
        height: _getItemHeight()! * 2.9,
        child: spinner(
          minuteController,
          _getMinuteCount(),
          currentSelectedMinuteIndex,
          isMinuteScrolling,
          widget.minutesInterval,
              (index) {
            currentSelectedMinuteIndex = index;
            isMinuteScrolling = true;
          },
              () => isMinuteScrolling = false,
        ),
      ),
    ];

    if (widget.isShowSeconds) {
      contents.add(spacer());
      contents.add(SizedBox(
        width: _getItemWidth(),
        height: _getItemHeight()! * 2.9,
        child: spinner(
          secondController,
          _getSecondCount(),
          currentSelectedSecondIndex,
          isSecondsScrolling,
          widget.secondsInterval,
              (index) {
            currentSelectedSecondIndex = index;
            isSecondsScrolling = true;
          },
              () => isSecondsScrolling = false,
        ),
      ));
    }

    if (!widget.is24HourMode) {
      contents.add(const SizedBox(width: Dimensions.paddingSizeSmall,));
      contents.add(Container(
        height: _getItemHeight()! * 2.9, width: _getItemWidth()! * 1.8,
        margin: EdgeInsets.only(left: Dimensions.paddingSizeSmall),
        decoration: BoxDecoration(
          borderRadius: BorderRadius.circular(Dimensions.paddingSizeExtraSmall),
          color: Theme.of(context).cardColor,
        ),
        child: apSpinner(),
      ));
    }

    return Row(
      crossAxisAlignment: CrossAxisAlignment.center,
      mainAxisAlignment: MainAxisAlignment.center,
      children: contents,
    );
  }

  Widget spacer() {
    return SizedBox(
      width: _getSpacing(),
      height: _getItemHeight()! * 3,
    );
  }

  Widget spinner(
      ScrollController controller,
      int max,
      int selectedIndex,
      bool isScrolling,
      int interval,
      SelectedIndexCallback onUpdateSelectedIndex,
      VoidCallback onScrollEnd
      ) {

    Widget spinner = NotificationListener<ScrollNotification>(
      onNotification: (scrollNotification) {
        if (scrollNotification is UserScrollNotification) {
          if (scrollNotification.direction.toString() ==
              "ScrollDirection.idle") {
            if (isLoop(max)) {
              int segment = (selectedIndex / max).floor();
              if (segment == 0) {
                onUpdateSelectedIndex(selectedIndex + max);
                controller.jumpTo(controller.offset + (max * _getItemHeight()!));
              } else if (segment == 2) {
                onUpdateSelectedIndex(selectedIndex - max);
                controller.jumpTo(controller.offset - (max * _getItemHeight()!));
              }
            }
            setState(() {
              onScrollEnd();
              widget.scrollOff(true);
              if (widget.onTimeChange != null) {
                widget.onTimeChange!(getDateTime());
              }
            });
          }
        } else if (scrollNotification is ScrollUpdateNotification) {
          setState(() {
            widget.scrollOff(false);
            onUpdateSelectedIndex(
                (controller.offset / _getItemHeight()!).round() + 1);
          });
        }
        return true;
      },
      child: Container(
        decoration: BoxDecoration(
            color: Theme.of(context).cardColor,
            borderRadius: BorderRadius.circular(Dimensions.radiusDefault)
        ),
        child: ScrollConfiguration(
          behavior: ScrollConfiguration.of(context).copyWith(scrollbars: false),
          child: ListView.builder(
            itemBuilder: (context, index) {
              String text = '';
              if (isLoop(max)) {
                text = ((index % max) * interval).toString();
              } else if (index != 0 && index != max + 1) {
                text = (((index - 1) % max) * interval).toString();
              }
              if (!widget.is24HourMode && controller == hourController && text == '0') {
                text = '12';
              }
              if (widget.isForce2Digits && text != '') {
                text = text.padLeft(2, '0');
              }
              return Container(
                height: _getItemHeight(),
                alignment: _getAlignment(),
                child: Center(
                  child: Text(
                    text,
                    style: selectedIndex == index
                        ? _getHighlightedTextStyle()
                        : _getNormalTextStyle(),
                  ),
                ),
              );
            },
            controller: controller,
            itemCount: isLoop(max) ? max * 3 : max + 2,
            physics: ItemScrollPhysics(itemHeight: _getItemHeight(),targetPixelsLimit: 0),
            padding: EdgeInsets.zero,
          ),
        ),
      ),
    );

    return Stack(
      children: <Widget>[
        Positioned.fill(child: spinner),
        isScrolling
            ? Positioned.fill(
            child: Container(
              color: Colors.black.withValues(alpha: 0),
            ))
            : Container()
      ],
    );
  }

  Widget apSpinner() {
    Widget spinner = NotificationListener<ScrollNotification>(
      onNotification: (scrollNotification) {
        if (scrollNotification is UserScrollNotification) {
          if (scrollNotification.direction.toString() == "ScrollDirection.idle") {
            isAPScrolling = false;
            if (widget.onTimeChange != null) {
              widget.onTimeChange!(getDateTime());
            }
          }
        } else if (scrollNotification is ScrollUpdateNotification) {
          setState(() {
            currentSelectedAPIndex = (apController.offset / _getItemHeight()!).round() + 1;
            isAPScrolling = true;
          });
        }
        return true;
      },
      child: ListView.builder(
        itemBuilder: (context, index) {
          String text = index == 1 ? 'AM' : (index == 2 ? 'PM' : '');
          return Container(
            height: _getItemHeight(),
            alignment: Alignment.center,
            child: Text(
              text,
              style: currentSelectedAPIndex == index ? _getHighlightedTextStyle() : _getNormalTextStyle(),
            ),
          );
        },
        controller: apController,
        itemCount: 4,
        physics: ItemScrollPhysics(
          itemHeight: _getItemHeight(),
          targetPixelsLimit: 1,
        ),
      ),
    );

    return Stack(
      children: <Widget>[
        Positioned.fill(child: spinner),
        isAPScrolling ? Positioned.fill(child: Container()) : Container()
      ],
    );
  }
}

