class TripCancellationCauseList {
  String? responseCode;
  String? message;
  String? totalSize;
  String? limit;
  String? offset;
  Data? data;
  List<String>? errors;

  TripCancellationCauseList(
      {this.responseCode,
        this.message,
        this.totalSize,
        this.limit,
        this.offset,
        this.data,
        this.errors});

  TripCancellationCauseList.fromJson(Map<String, dynamic> json) {
    responseCode = json['response_code'];
    message = json['message'];
    totalSize = json['total_size'];
    limit = json['limit'];
    offset = json['offset'];
    data = json['data'] != null ?  Data.fromJson(json['data']) : null;
    errors = json['errors'].cast<String>();
  }

}

class Data {
  List<String>? ongoingRide;
  List<String>? acceptedRide;

  Data({this.ongoingRide, this.acceptedRide});

  Data.fromJson(Map<String, dynamic> json) {
    ongoingRide = json['ongoing_ride'].cast<String>();
    acceptedRide = json['accepted_ride'].cast<String>();
  }

  Map<String, dynamic> toJson() {
    final Map<String, dynamic> data =  <String, dynamic>{};
    data['ongoing_ride'] = ongoingRide;
    data['accepted_ride'] = acceptedRide;
    return data;
  }
}

import 'package:ride_sharing_user_app/features/ride/domain/models/trip_details_model.dart';

class TripModel {
  int? totalSize;
  String? limit;
  String? offset;
  List<TripDetails>? data;

  TripModel(
      {
        this.totalSize,
        this.limit,
        this.offset,
        this.data,
        });

  TripModel.fromJson(Map<String, dynamic> json) {
    totalSize = json['total_size'];
    limit = json['limit'];
    offset = json['offset'];
    if (json['data'] != null) {
      data = <TripDetails>[];
      json['data'].forEach((v) {
        data!.add(TripDetails.fromJson(v));
      });
    }

  }

}

class TripOverView {
  int? successRate;
  int? totalTrips;
  int? totalEarn;
  int? totalCancel;
  int? totalReviews;
  IncomeStat? incomeStat;

  TripOverView(
      {this.successRate,
        this.totalTrips,
        this.totalEarn,
        this.totalCancel,
        this.totalReviews,
        this.incomeStat});

  TripOverView.fromJson(Map<String, dynamic> json) {
    successRate = json['success_rate'];
    totalTrips = json['total_trips'];
    totalEarn = json['total_earn'];
    totalCancel = json['total_cancel'];
    totalReviews = json['total_reviews'];
