        widget.paymentType != null ?
        Text(widget.paymentType!,style: textRegular.copyWith(color: Theme.of(context).primaryColor)) :
        widget.discount ?
        Text(
          '-${PriceConverter.convertPrice(widget.amount)}',
          style: textRobotoRegular,
        ) :
        Text(
          PriceConverter.convertPrice(widget.amount),
          style: textRobotoRegular,
        ),

      ]),
    );
  }
