class WalletController extends GetxController implements GetxService{
  final WalletService walletService;

  WalletController({required this.walletService});

  TextEditingController inputController = TextEditingController();
  FocusNode inputNode = FocusNode();
  ScrollController scrollController = ScrollController();

  bool isLoading = false;

  List<String> walletType = ['wallet_money','loyalty_point'];

  TransactionModel? transactionModel;
  Future<Response> getTransactionList(int offset) async {
    isLoading = true;
    update();
    Response? response = await walletService.getTransactionList(offset);
    if (response!.statusCode == 200) {
      isLoading = false;
      if(offset == 1){
        transactionModel = TransactionModel.fromJson(response.body);
      }else{
        transactionModel!.data!.addAll(TransactionModel.fromJson(response.body).data!);
        transactionModel!.offset = TransactionModel.fromJson(response.body).offset;
        transactionModel!.totalSize = TransactionModel.fromJson(response.body).totalSize;
      }

    }else{
      isLoading = false;
      ApiChecker.checkApi(response);
    }
    update();
    return response;
  }
  AddFundPromotionalModel? addFundPromotionalModel;
  LoyaltyPointModel? loyaltyPointModel;
  Future<Response> getLoyaltyPointList(int offset) async {
    isLoading = true;
    update();
    Response? response = await walletService.getLoyaltyPointList(offset);
    if (response!.statusCode == 200) {
      isLoading = false;
      if(offset == 1){
        loyaltyPointModel = LoyaltyPointModel.fromJson(response.body);
      }else{
        loyaltyPointModel!.data!.addAll(LoyaltyPointModel.fromJson(response.body).data!);
        loyaltyPointModel!.offset = LoyaltyPointModel.fromJson(response.body).offset;
        loyaltyPointModel!.totalSize = LoyaltyPointModel.fromJson(response.body).totalSize;
      }
    }else{
      isLoading = false;
      ApiChecker.checkApi(response);
    }
    update();
    return response;
  }



  Future<Response> convertPoint(String point) async {
    isLoading = true;
    update();
    Response? response = await walletService.convertPoint(point);
    if (response!.statusCode == 200) {
     Get.find<ProfileController>().getProfileInfo();
     getTransactionList(1);
      isLoading = false;
    }else{
      isLoading = false;
      ApiChecker.checkApi(response);
    }
    update();
    return response;
  }

  int currentTabIndex = 0;

  void updateCurrentTabIndex(int index, {bool isUpdate = false}){
    currentTabIndex = index;
    if(index == 0){
      getTransactionList(1);
    }else{
      getLoyaltyPointList(1);
    }
    if(isUpdate){
      update();
    }
  }

  Future<void> transferWalletMoney(String balance) async{
    isLoading = true;
    update();
    Response response = await walletService.transferWalletMoney(balance);
    if(response.statusCode == 200){
      isLoading = false;
      getTransactionList(1);
      Get.find<ProfileController>().getProfileInfo();
      Get.back();
      showCustomSnackBar('transfer_successfully'.tr,isError: false);
    }else{
      isLoading = false;
      ApiChecker.checkApi(response);
    }
    update();
  }

  Future<void> getAddFundPromotionalList() async{
    Response response = await walletService.getAddFundPromotionalList();
    if(response.statusCode == 200){
      addFundPromotionalModel = AddFundPromotionalModel.fromJson(response.body);
    }else{
      ApiChecker.checkApi(response);
    }
    update();
  }

}

class AddFundPromotionalModel {
  String? responseCode;
  String? message;
  int? totalSize;
  int? limit;
  int? offset;
  List<Data>? data;
  List<String>? errors;

  AddFundPromotionalModel({
    this.responseCode,
    this.message,
    this.totalSize,
    this.limit,
    this.offset,
    this.data,
    this.errors
  });

  AddFundPromotionalModel.fromJson(Map<String, dynamic> json) {
    responseCode = json['response_code'];
    message = json['message'];
    totalSize = json['total_size'];
    limit = json['limit'];
    offset = json['offset'];
    if (json['data'] != null) {
      data = <Data>[];
      json['data'].forEach((v) {
        data!.add(Data.fromJson(v));
      });
    }
    errors = json['errors'].cast<String>();
  }

  Map<String, dynamic> toJson() {
    final Map<String, dynamic> data = <String, dynamic>{};
    data['response_code'] = responseCode;
    data['message'] = message;
    data['total_size'] = totalSize;
    data['limit'] = limit;
    data['offset'] = offset;
    if (this.data != null) {
      data['data'] = this.data!.map((v) => v.toJson()).toList();
    }
    data['errors'] = errors;
    return data;
  }
}

class Data {
  String? id;
  String? name;
  String? description;
  double? bonusAmount;
  String? amountType;
  int? minAddAmount;
  int? maxBonusAmount;
  String? startDate;
  String? endDate;
  List<String>? userType;
  int? isActive;

  Data({
    this.id,
    this.name,
    this.description,
    this.bonusAmount,
    this.amountType,
    this.minAddAmount,
    this.maxBonusAmount,
    this.startDate,
    this.endDate,
    this.userType,
    this.isActive
  });

  Data.fromJson(Map<String, dynamic> json) {
    id = json['id'];
    name = json['name'];
    description = json['description'];
    bonusAmount = double.tryParse('${json['bonus_amount']}');
    amountType = json['amount_type'];
    minAddAmount = json['min_add_amount'];
    maxBonusAmount = json['max_bonus_amount'];
    startDate = json['start_date'];
    endDate = json['end_date'];
    userType = json['user_type'].cast<String>();
    isActive = json['is_active'];
  }

  Map<String, dynamic> toJson() {
    final Map<String, dynamic> data = <String, dynamic>{};
    data['id'] = id;
    data['name'] = name;
    data['description'] = description;
    data['bonus_amount'] = bonusAmount;
    data['amount_type'] = amountType;
    data['min_add_amount'] = minAddAmount;
    data['max_bonus_amount'] = maxBonusAmount;
    data['start_date'] = startDate;
    data['end_date'] = endDate;
    data['user_type'] = userType;
    data['is_active'] = isActive;
    return data;
  }
}

class LoyaltyPointModel {
  String? responseCode;
  String? message;
  int? totalSize;
  String? limit;
  String? offset;
  List<Points>? data;


  LoyaltyPointModel(
      {this.responseCode,
        this.message,
        this.totalSize,
        this.limit,
        this.offset,
        this.data
      });

  LoyaltyPointModel.fromJson(Map<String, dynamic> json) {
    responseCode = json['response_code'];
    message = json['message'];
    totalSize = json['total_size']??0;
    if(json['limit'] != null){
      limit = json['limit'].toString();
    }else{
      limit = '10';
    }
    if(json['offset']!=null){
      offset = json['offset'].toString();
    }else{
      offset = '1';
    }

    if (json['data'] != null) {
      data = <Points>[];
      json['data'].forEach((v) {
        data!.add(Points.fromJson(v));
      });
    }

  }

}

class Points {
  String? userId;
  String? model;
  String? modelId;
  int? points;
  String? type;
  String? createdAt;

  Points(
      {this.userId,
        this.model,
        this.modelId,
        this.points,
        this.type,
        this.createdAt});

  Points.fromJson(Map<String, dynamic> json) {
    userId = json['user_id'];
    model = json['model'];
    modelId = json['model_id'];
    if(json['points'] != null){
      points = json['points'];
    }else{
      points = 0;
    }

    type = json['type'];
    createdAt = json['created_at'];
  }

}

class TransactionModel {
  String? responseCode;
  String? message;
  int? totalSize;
  String? limit;
  String? offset;
  List<Transaction>? data;


  TransactionModel(
      {this.responseCode,
        this.message,
        this.totalSize,
        this.limit,
        this.offset,
        this.data,
        });

  TransactionModel.fromJson(Map<String, dynamic> json) {
    responseCode = json['response_code'];
    message = json['message'];
    totalSize = json['total_size'];
    limit = json['limit'];
    offset = json['offset'];
    if (json['data'] != null) {
      data = <Transaction>[];
      json['data'].forEach((v) {
        data!.add(Transaction.fromJson(v));
      });
    }

  }

}

class Transaction {
  String? id;
  String? attribute;
  String? attributeId;
  double? debit;
  double? credit;
  String? createdAt;

  Transaction(
      {this.id,
        this.attribute,
        this.attributeId,
        this.debit,
        this.credit,
        this.createdAt});

  Transaction.fromJson(Map<String, dynamic> json) {
    id = json['id'];
    attribute = json['attribute'];
    attributeId = json['attribute_id'];
    debit = json['debit'].toDouble();
    credit = json['credit'].toDouble();
    createdAt = json['created_at'];
  }


}

