import 'package:ride_sharing_user_app/features/address/domain/models/address_model.dart';
import 'package:ride_sharing_user_app/localization/language_model.dart';

import 'package:flutter/material.dart';
import 'package:get/get.dart';
import 'package:shared_preferences/shared_preferences.dart';
import 'package:intl/intl.dart' as intl;

import '../core/constants/app_constants.dart';
import '../core/network/api_client.dart';

class LocalizationController extends GetxController implements GetxService {
  final SharedPreferences sharedPreferences;

  LocalizationController({required this.sharedPreferences}) {
    loadCurrentLanguage();
  }

  Locale _locale = Locale(AppConstants.languages[0].languageCode, AppConstants.languages[0].countryCode);
  bool _isLtr = true;
  int _selectIndex = 0;
  List<LanguageModel> _languages = [];

  Locale get locale => _locale;
  bool get isLtr => _isLtr;
  int get selectIndex => _selectIndex;
  List<LanguageModel> get languages => _languages;
  bool isLoading = false;

  void setLanguage(Locale locale) {
    Get.updateLocale(locale);
    _locale = locale;
    _isLtr = !intl.Bidi.isRtlLanguage(_locale.languageCode);
    saveLanguage(_locale);
    isLoading = true;
    update();

    Address? address;
    try {
      address = Address.fromJson(jsonDecode(sharedPreferences.getString(AppConstants.userAddress)!));
      // ignore: empty_catches
    }catch(e) {
    }
    Get.find<ApiClient>().updateHeader(sharedPreferences.getString(AppConstants.token) ?? '', address);
    backendLanguageUpdate();
  }

  void loadCurrentLanguage() async {
    if(sharedPreferences.containsKey(AppConstants.languageCode)){
      _locale = Locale(sharedPreferences.getString(AppConstants.languageCode)!,
          sharedPreferences.getString(AppConstants.countryCode));
      _isLtr = !intl.Bidi.isRtlLanguage(_locale.languageCode);
      update();
    }
  }

  void saveLanguage(Locale locale) async {
    sharedPreferences.setString(AppConstants.languageCode, locale.languageCode);
    sharedPreferences.setString(AppConstants.countryCode, locale.countryCode!);
    update();
  }

  void setSelectIndex(int index) {
    _selectIndex = index;
    update();
  }

  void searchLanguage(String query, BuildContext context) {
    if (query.isEmpty) {
      _languages.clear();
      _languages = AppConstants.languages;
      update();
    } else {
      _selectIndex = -1;
      _languages = [];
      for (LanguageModel language in AppConstants.languages) {
        if (language.languageName.toLowerCase().contains(query.toLowerCase())) {
          _languages.add(language);
        }
      }
      update();
    }
  }

  void initializeAllLanguages(BuildContext context) {
    if (_languages.isEmpty) {
      _languages.clear();
      _languages = AppConstants.languages;
    }
  }

  void setInitialIndex(){
    for(int i = 0; i < AppConstants.languages.length; i++){
      if(locale.languageCode == AppConstants.languages[i].languageCode){
        _selectIndex = i;
      }
    }
  }

  void backendLanguageUpdate(){
    Get.find<ApiClient>().postData(AppConstants.changeLanguage, {});
  }

  bool haveLocalLanguageCode() {
    if(sharedPreferences.containsKey(AppConstants.languageCode)) {
      return true;
    }else{
      return false;
    }
  }

}

import 'package:get/get.dart';

class Messages extends Translations {
  final Map<String, Map<String, String>> languages;
  Messages({required this.languages});

  @override
  Map<String, Map<String, String>> get keys {
    return languages;
  }
}

import 'package:firebase_messaging/firebase_messaging.dart';
import 'package:flutter/material.dart';
import 'package:flutter/services.dart';
import 'package:flutter_local_notifications/flutter_local_notifications.dart';
import 'package:ride_sharing_user_app/app.dart';
import 'package:ride_sharing_user_app/core/config/firebase_config.dart';
import 'package:ride_sharing_user_app/helper/notification_helper.dart';
// If init() is undefined, verify that di_container.dart actually has a function named 'init'
import 'package:ride_sharing_user_app/helper/di_container.dart' as di;

final FlutterLocalNotificationsPlugin flutterLocalNotificationsPlugin = FlutterLocalNotificationsPlugin();

Future<void> main() async {
  try {
    WidgetsFlutterBinding.ensureInitialized();

    // Initialize Firebase
    await FirebaseConfig.initialize();

    // Initialize dependencies (DI)
    // If 'init' is showing red, check di_container.dart for spelling errors
    Map<String, Map<String, String>> languages = await di.init();

    // Get initial notification
    final RemoteMessage? remoteMessage = await FirebaseMessaging.instance.getInitialMessage();

    // Setup notifications
    await NotificationHelper.initialize(flutterLocalNotificationsPlugin);
    FirebaseMessaging.onBackgroundMessage(myBackgroundMessageHandler);

    // Lock orientation
    await SystemChrome.setPreferredOrientations([DeviceOrientation.portraitUp]);

    runApp(MyApp(
      languages: languages,
      notificationData: remoteMessage?.data,
    ));
  } catch (e) {
    debugPrint(' Error initializing app: $e');
    runApp(const MaterialApp(home: Scaffold(body: Center(child: Text('Failed to initialize app.')))));
  }
}
